import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { addPosition } from "../store/portfolioReducer";
import "./Form.css";

const AddPositionForm = () => {
  const dispatch = useDispatch();

  const [currency, setCurrency] = useState("EUR");
  const [amount, setAmount] = useState("");
  const [date, setDate] = useState("");
  const [price, setPrice] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!amount || !date || !price) {
      alert("Uzupełnij wszystkie pola!");
      return;
    }

    dispatch(
      addPosition({
        currency,
        amount: parseFloat(amount),
        date,
        purchasePrice: parseFloat(price),
      })
    );

    setAmount("");
    setDate("");
    setPrice("");
  };
  useEffect(() => {
    const fetchHistoricalRate = async () => {
      if (!date || !currency) return;

      try {
        const response = await fetch(
          `https://api.exchangeratesapi.io/v1/${date}?access_key=90e27173df1896dcb85ccf461a486342`
        );
        const data = await response.json();

        if (data?.rates && data.rates[currency]) {
          setPrice(data.rates[currency].toFixed(4));
        }
      } catch (e) {
        console.error("Błąd pobierania kursu historycznego:", e);
      }
    };

    fetchHistoricalRate();
  }, [currency, date]);

  return (
    <form onSubmit={handleSubmit}>
      {" "}
      <div className="form-field">
        <label>
          Waluta:
          <select
            value={currency}
            onChange={(e) => setCurrency(e.target.value)}
          >
            <option value="EUR">EUR</option>
            <option value="USD">USD</option>
            <option value="PLN">PLN</option>
          </select>
        </label>
      </div>{" "}
      <div className="form-field">
        <label>
          Ilość:
          <input
            type="number"
            step="any"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
        </label>
      </div>{" "}
      <div className="form-field">
        <label>
          Data zakupu:
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
          />
        </label>
      </div>{" "}
      <div className="form-field">
        <label>
          Cena zakupu:
          <input
            type="number"
            step="any"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
          />
        </label>
      </div>
      <button type="submit">Dodaj</button>
    </form>
  );
};

export default AddPositionForm;
